      if (mm < 10) mmstr = '0' + mmstr;
      var dd = mydate.getDate();
      var ddstr = dd.toString();
      if (dd < 10) ddstr = '0' + ddstr;
      return yyyy + '-' + mmstr + '-' + ddstr;
