#HistoricalData {
  overflow: hidden;
  text-align: left;
}
.border-top-gray {
  border-top: 1px solid #ebedf4;
  .second-title {
    padding: 30px 31px 8px;
    font-family: PingFangSC-Regular;
    font-size: 20px;
    color: #31394d;
    letter-spacing: 0;
    &.echartsTime {
      border-bottom: 1px solid #ebedf4;
    }
  }
}
.history {
  .history-message {
    padding: 0 31px 26px;
    .input-time-line {
      padding: 15px;
    }
  }
  .check-list {
    overflow: hidden;
    display: block;
  }
  .submit-button {
    display: block;
    text-align: center;
    margin: 26px auto 10px;
    .clear-title {
      padding-right: 20px;
      font-family: PingFangSC-Regular;
      font-size: 14px;
      color: rgba(0, 0, 0, 0.65);
      letter-spacing: 0;
    }
    .el-button--success {
      border-color: #00cc99;
      background-color: #00cc99;
    }
  }
}
//===el-carousel===

.el-carousel-record {
  &.el-carousel {
    @media (min-width: 998px) {
      margin: 20px 74px 71px;
      width: 82%;
    }
    @media (min-width: 1024px) {
      margin: 20px 84px 71px;
      width: 80.5%;
    }
    @media (min-width: 1050px) {
      margin: 20px 96px 71px;
      width: 78%;
    }
    @media (min-width: 1070px) {
      margin: 20px 105px 71px;
      width: 75%;
    }
    @media (min-width: 1280px) {
      margin: 20px 115px 71px;
      width: 75%;
    }
    .carousel-text {
      position: relative;
      top: -28px;
      left: -5px;
      text-align: right;
      .img-time {
        font-family: PingFangSC-Regular;
        font-size: 14px;
        color: rgba(255, 255, 255, 0.8);
        letter-spacing: 0;
        padding-right: 10px;
      }
      .img-load {
        opacity: 0.8;
        font-family: PingFangSC-Regular;
        font-size: 14px;
        color: #ffffff;
        text-align: left;
      }
      .el-icon-download {
        color: rgba(255, 255, 255, 0.8);
        border-radius: 1px;
      }
    }
  }
}

.ipcam_slide_show {
  width: 640px;
  height: 360px;
}
