#deviceStatus {
  overflow: hidden;
  text-align: left;
}
.mt-27 {
  margin-top: 27px;
}

.switch {
  padding: 40px 30px;
  .switch-content {
    width: 176px;
    height: 120px;
    padding: 16px 18px;
    line-height: 22px;

    border-radius: 5px;
    .switch-first {
      font-family: PingFangSC-Regular;
      font-size: 14px;
      letter-spacing: 0;
    }
    .switch-stats {
      font-family: PingFangSC-Regular;
      font-size: 18px;
      letter-spacing: 0;
    }
    .switch-retry {
      display: none;
      width: 65%;
      position: relative;
      top: -18px;
      opacity: 0.8;
      font-family: PingFangSC-Regular;
      font-size: 12px;
      color: #ffffff;
      letter-spacing: 0;
      text-align: right;
    }
    .switch-button {
      font-family: PingFangSC-Regular;
      font-size: 12px;
      letter-spacing: 0;
      border-radius: 2px;
      margin: 14px 0 0;
      padding: 2px 20px;
      background-color: transparent;
    }
  }
  .btn-status {
    cursor: pointer;
    font-family: PingFangSC-Medium;
    font-size: 12px;
    color: #ffffff;
    letter-spacing: 0;
    line-height: 16px;
    .btn-status-left {
      float: left;
    }
    .btn-status-right {
      float: right;
      span {
        margin-left: 18px;
      }
      i {
        position: absolute;
        width: 5%;
        height: 15%;
        &.switchAutomationImgNorth,
        &.switchAutomationImgSoutheast,
        &.switchAutomationImgSouthwest,
        &.switchAutomationImgNorths,
        &.switchAutomationImgSouths {
          background: url('./../assets/img/automatic.png') no-repeat;
        }
        &.switchManualImgNorth,
        &.switchManualImgSoutheast,
        &.switchManualImgSouthwest,
        &.switchManualImgNorths,
        &.switchManualImgSouths {
          background: url('./../assets/img/manua.png') no-repeat;
        }
      }
    }
  }
  .btn-status-switch {
    float: right;
    margin: 14px 0 0;
    padding: 2px 0;
    font-family: PingFangSC-Regular;
    font-size: 12px;
    color: #009966;
    letter-spacing: 0;
    line-height: 16px;
    cursor: pointer;
    span {
      margin-left: 16px;
    }
    i {
      position: absolute;
      width: 5%;
      height: 15%;
      &.switchManualImgPump,
      &.switchManualImgCooling,
      &.switchManualImgLighting {
        background: url('./../assets/img/manual_green.png') no-repeat;
      }
    }
    &.offActiveCooling,
    &.offActiveLighting,
    &.offActivePump {
      color: #fff;
      i {
        &.switchManualImgPump,
        &.switchManualImgCooling,
        &.switchManualImgLighting {
          background: url('./../assets/img/manua.png') no-repeat;
        }
      }
    }
  }
}
