#dashboard {
  overflow: hidden;
}
.el-icon {
  width: 25px;
  height: 25px;
  margin-right: 8px;
  // 氣壓
  &.el-icon-airPressure {
    background: url('../assets/img/air_pressure.png') no-repeat center;
    background-size: 100%;
  }
  // 溫度 溼度
  &.el-icon-temperature {
    background: url('../assets/img/temperature.png') no-repeat center;
    background-size: 100%;
  }
  // 紫外線、照度(日射量)
  &.el-icon-illumination {
    background: url('../assets/img/illumination.png') no-repeat center;
    background-size: 100%;
  }
  // 感雨、雨量
  &.el-icon-rainfall {
    background: url('../assets/img/rainfall.png') no-repeat center;
    background-size: 90%;
  }
  // 風向、風速
  &.el-icon-windDirection {
    background: url('../assets/img/wind_direction.png') no-repeat center;
    background-size: 100%;
  }
  // 土壤水份
  &.el-icon-soilMoisture {
    background: url('../assets/img/soil_moisture.png') no-repeat center;
    background-size: 90%;
  }
  // 土壤溫度
  &.el-icon-soilTemperature {
    background: url('../assets/img/soil_temperature.png') no-repeat center;
    background-size: 85%;
  }
  // 土壤導電率(土壤鹽份)
  &.el-icon-soilConductivity {
    background: url('../assets/img/soil_conductivity.png') no-repeat center;
    background-size: 100%;
  }
  // 水位計
  &.el-icon-waterLevel {
    background: url('../assets/img/water_level.png') no-repeat center;
    background-size: 100%;
  }
  // PH值(酸鹼度)
  &.el-icon-PH {
    background: url('../assets/img/ph.png') no-repeat center;
    background-size: 90%;
  }
  // Co2
  &.el-icon-Co2 {
    background: url('../assets/img/co2.png') no-repeat center;
    background-size: 100%;
  }
  // 風扇
  &.el-icon-Fan {
    background: url('../assets/img/fan.png') no-repeat center;
    background-size: 80%;
  }
  // LED燈
  &.el-icon-LED {
    background: url('../assets/img/led.png') no-repeat center;
    background-size: 70%;
  }
  // 天窗、側窗
  &.el-icon-skylight {
    background: url('../assets/img/skylight.png') no-repeat center;
    background-size: 90%;
  }
  // 窗簾
  &.el-icon-curtain {
    background: url('../assets/img/curtain.png') no-repeat center;
    background-size: 90%;
  }
  // 灌水PUMP
  &.el-icon-PUMP {
    background: url('../assets/img/pump.png') no-repeat center;
    background-size: 85%;
  }
  // 肥料裝置
  &.el-icon-fertilizer {
    background: url('../assets/img/fertilizer.png') no-repeat center;
    background-size: 85%;
  }
}
.border-bottom-no {
  border-bottom: 1px solid transparent !important;
}
.content {
  $bg-sun-color: rgba(247, 222, 109, 0.3);
  $bg-cloudy-color: #f5eecf;
  $bg-rain-color: #b4dff4;
  $bg-snow-color: #d7dade;
  $bg-overcast-color: #d7dade;
  .left-message {
    .content-block {
      .weather {
        overflow: hidden;
        float: left;
        width: 205px;
        border-top-left-radius: 0.3rem;
        border-bottom-left-radius: 0.3rem;
        padding: 30px 27px;

        //sun
        &.weather-sun {
          background-color: $bg-sun-color;
          //2
          .weather-img {
            background: url(../assets/img/sunny_day.png) no-repeat center;
            background-size: 90px 90px;
          }
        }
        //cloudy
        &.weather-cloudy {
          background-color: $bg-cloudy-color;
          //2
          .weather-img {
            background: url(../assets/img/cloudy_day.png) no-repeat center;
            background-size: 90px 90px;
          }
        }
        //rain
        &.weather-rain {
          background-color: $bg-rain-color;
          //2
          .weather-img {
            background: url(../assets/img/rain_day.png) no-repeat center;
            background-size: 90px 90px;
          }
        }
        //snow
        &.weather-snow {
          background-color: $bg-snow-color;
          //2
          .weather-img {
            background: url(../assets/img/snow_day.png) no-repeat center;
            background-size: 90px 90px;
          }
        }
        //overcastDay
        &.weather-overcast {
          background-color: $bg-overcast-color;
          //2
          .weather-img {
            background: url(../assets/img/overcast_day.png) no-repeat center;
            background-size: 90px 90px;
          }
        }
        //1
        .weather-title {
          font-size: 26px;
          color: rgba(0, 0, 0, 0.65);
          letter-spacing: 0;
          line-height: 30px;
          text-align: left;
        }
        //2
        .weather-img {
          height: 240px;
        }
        //3
        .weather-time {
          overflow: hidden;
          float: left;
          .temperature {
            float: left;
            width: 35%;
            font-family: DINCondensed-Bold;
            font-size: 60px;
            color: rgba(0, 0, 0, 0.65);
            letter-spacing: 0;
            line-height: 60px;
            text-align: left;
            small {
              display: block;
              font-family: PingFangSC-Regular;
              font-size: 12px;
              color: #31394d;
              letter-spacing: 0;
              line-height: 12px;
              text-align: center;
            }
          }
          .temperature-time {
            position: relative;
            top: 35px;
            float: right;
            width: 65%;
            text-align: right;
            font-family: PingFangSC-Regular;
            font-size: 16px;
            color: rgba(0, 0, 0, 0.65);
            letter-spacing: -0.56px;
            small {
              display: block;
              opacity: 0.85;
              font-family: PingFangSC-Regular;
              font-size: 12px;
              color: rgba(0, 0, 0, 0.65);
              letter-spacing: -0.33px;
            }
          }
        }
      }

      .weather-content {
        float: right;
        padding: 32px 40px 32px 0;
        width: 680px;
        @media (min-width: 998px) {
          width: 680px;
          padding: 32px 80px 32px 0;
        }
        @media (min-width: 1024px) {
          width: 685px;
          padding: 32px 60px 32px 0;
        }
        @media (min-width: 1050px) {
          width: 710px;
          padding: 32px 45px 32px 15px;
        }
        @media (min-width: 1070px) {
          width: 725px;
          padding: 32px 20px;
        }
        @media (min-width: 1280px) {
          padding: 32px 35px;
          width: 740px;
        }
        .weather-content-border {
          overflow: hidden;
          width: 187px;
          padding: 12px 0 6px;
          border-bottom: 1px solid #e8e8e8;
          .weather-text {
            position: relative;
            bottom: 6px;
            font-size: 14px;
            color: rgba(0, 0, 0, 0.65);
            line-height: 20px;
          }
          .weather-text-math {
            position: relative;
            bottom: -3px;
            font-size: 14px;
            color: #595959;
          }
        }
        .weather-content-gray {
          margin-top: 12px;
          opacity: 0.85;
          font-family: PingFangSC-Regular;
          font-size: 12px;
          color: rgba(0, 0, 0, 0.35);
          letter-spacing: -0.33px;
          text-align: left;
        }
        .weather-content-img {
          img {
            border-radius: 5px;
            width: 100%;
          }
        }
      }
    }
  }
}
